import React, { useState } from 'react';

// ViewItems Component
const ViewItems = ({ todoId, itemData, markDone, deleteItem }) => {
  return (
    <div>
      <h2>{itemData.name} : {itemData.priority}</h2>
      <input 
        type="checkbox" 
        checked={itemData.completed} 
        onChange={() => markDone(todoId, !itemData.completed)} 
      />
      <button onClick={() => deleteItem(todoId)}>Delete</button>
    </div>
  );
};

// NewItem Component
const NewItem = ({ addTodo }) => {
  const [name, setName] = useState("");
  const [priority, setPriority] = useState("low");

  return (
    <div>
      <input 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        placeholder="Enter todo name"
      />
      <select value={priority} onChange={(e) => setPriority(e.target.value)}>
        <option value="low">Low</option>
        <option value="mid">Mid</option>
        <option value="high">High</option>
      </select>
      <button onClick={() => {
        addTodo(name, priority);
        setName("");
        setPriority("low");
      }}>
        Add Todo
      </button>
    </div>
  );
};

// App Component
const App = () => {
  const [todos, setTodos] = useState({});

  const addTodo = (name, priority) => {
    // Fill in your addTodo logic here
    const id = `${Math.floor(Math.random() * 1000)}_${Date.now()}`;
    const dateAdded = new Date().toISOString().split('T')[0];
    const completed = false;

    const newTodo = {
      name,
      dateAdded,
      priority,
      completed
    };

    setTodos({
      ...todos,
      [id]: newTodo
    });
  };

  const markDone = (todoId, newCompletedStatus) => {
    // Fill in your markDone logic here
    const currentTodo = todos[todoId];
    const updatedTodo = {
      ...currentTodo,
      completed: newCompletedStatus
    };
    setTodos({
      ...todos,
      [todoId]: updatedTodo
    });
  };

  const deleteItem = (todoId) => {
    // Fill in your deleteItem logic here
    const filteredEntries = Object.entries(todos).filter((item) => item[0] !== todoId);
    const newTodos = Object.fromEntries(filteredEntries);
    setTodos(newTodos);
  };

  return (
    <div>
      <h1>My Todo App</h1>
      <NewItem addTodo={addTodo} />

      <div>
        <h2>Todo List:</h2>
        {Object.entries(todos).map(([todoId, itemData]) => (
          <ViewItems 
            key={todoId}
            todoId={todoId}
            itemData={itemData}
            markDone={markDone}
            deleteItem={deleteItem}
          />
        ))}
      </div>
    </div>
  );
};

export default App;
